mapper.update:
UPDATE member_tb
SET
    member_email_yn = #{member_email_yn}
WHERE
    member_email = #{member_email}

UPDATE member_tb
SET
    member_state = #{member_state}
WHERE
    member_idx = #{member_idx}

UPDATE member_tb
SET
    member_pw = #{member_pw},
    member_phone = #{member_phone}
WHERE
    member_idx = #{member_idx}
